/* eslint-disable */
import React, { useState } from 'react';
import './Search.css';

import handleSearch from './handle-search';

const Search = props => {
  const { setMovieList } = props;
  if (!setMovieList) {
    throw Error('Search component missing setMovieList prop.');
  }

  const [searchState, setSearchState] = useState({ search: '' });

  const resetSearch = () => {
    setSearchState({ search: '' });
  };

  const handleInput = event => {
    setSearchState({
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async event => {
    event.preventDefault();
    const searchResults = await handleSearch(searchState.search);
    setMovieList(searchResults);
    resetSearch();
  };

  return (
    <section className="container-fluid search-section">
      <form className="form">
        <input
          name="search"
          className="form-control mr-sm-2 movie-search-input"
          type="text"
          placeholder="Search Movies, TV Shows, People"
          autoComplete="off"
          onChange={handleInput}
          value={searchState.search}
        />
        <button
          className="btn btn-lg btn-outline-success my-sm-2 btn-search"
          type="submit"
          onClick={handleSubmit}
        >
          Search
        </button>
        <button
          className="btn btn-lg btn-outline-success my-sm-2 btn-search"
          type="button"
          onClick={resetSearch}
        >
          Reset
        </button>
      </form>
    </section>
  );
};

export default Search;
